    private void cmdCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cmdCalcularActionPerformed

        String Np, Tl, Nc, Mp;
        int Pala, Tama, Lorco, Oper1, Oper2, Oper3, Total;
        txtPagar.setText("");
        if (txtNC.getText().trim().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Por Favor Diga Cuanto Colores Necesita", "Error", JOptionPane.ERROR_MESSAGE);
            txtNC.requestFocusInWindow();
        } else if (txtNP.getText().trim().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Por Favor Diga Cuantas Palabras Utilizara", "Error", JOptionPane.ERROR_MESSAGE);
            txtNP.requestFocusInWindow();
        } else if (txtTA.getText().trim().isEmpty()) {
            JOptionPane.showMessageDialog(this, "Por Favor Diga Su Tamaño", "Error", JOptionPane.ERROR_MESSAGE);
            txtTA.requestFocusInWindow();
        } else {
            
            Pala = Integer.parseInt(txtNP.getText());
            Tama = Integer.parseInt(txtTA.getText());
            Lorco = Integer.parseInt(txtNC.getText());
            Oper1 = Pala * 20000;
            Oper2 = Tama * 15000;
            Oper3 = Lorco * 25000;
            Total = Oper1 + Oper2 + Oper3;
            Mp = String.valueOf(Total);
            txtPagar.setText(Mp);
        }
    }//GEN-LAST:event_cmdCalcularActionPerformed

